    const url = pm.variables.replaceIn(pm.request.url.toString());
    console.log(url);
    const method = pm.request.method;
    const time = moment.utc();
    const date = time.format('ddd, DD MMM YYYY HH:mm:ss [GMT]');
    const txDate = time.format('YYYY-MM-DDTHH:mm:ssZ');
