    console.log(pm.collectionVariables.get("transaction-date"));
    const body = removeComments(pm.variables.replaceIn(pm.request.body.raw) || "");
    console.log(body);
    const signature = getSignature(clientId, privateKey, url, method, body, date);

    pm.collectionVariables.set("x-pti-signature", signature);
}

