script.exec:
const moment = require('moment');
const uuid = require("uuid");
const urlParser = require('url');

// Simulate the window & navigator objects for the jsrsasign lirbrary
var window = {};
var navigator = {};

function loadJsrsasignLibrary() {
    return new Promise((resolve, reject) => {
        if (pm.collectionVariables.get("jsrsasign_library")) {
            //console.log("Library jsrsasign already downloaded");
            eval(pm.collectionVariables.get("jsrsasign_library"));
            pm.collectionVariables.set('KJUR', KJUR);
            pm.collectionVariables.set('KEYUTIL', KEYUTIL);
            resolve();
        } else {
            pm.sendRequest("https://cdnjs.cloudflare.com/ajax/libs/jsrsasign/11.1.0/jsrsasign-all-min.js", (err, res) => {
                if (err) {
                    console.error("Error downloading library jsrsasign:", err);
                    reject(err);
                    return;
                }
                console.log("Downloaded library jsrsasign successfully");
                pm.collectionVariables.set("jsrsasign_library", res.text());
                eval(pm.collectionVariables.get("jsrsasign_library"));
                pm.collectionVariables.set('KJUR', KJUR);
                pm.collectionVariables.set('KEYUTIL', KEYUTIL);
                resolve();
            });
        }
    });
}

function getSignature(clientId, key, url, method, body, date) {
    let payload = `${method}\n`;
    if (["POST", "PUT", "PATCH"].includes(method)) {
        const KJUR = pm.collectionVariables.get('KJUR');
        payload += `${KJUR.crypto.Util.sha256(body || "").toUpperCase()}\n`;
        payload += "content-type:" + pm.request.headers.get("Content-Type") + "\n";
    } else {
        payload += "\n\n";
    }
    payload += `date:${date}\n`;
    payload += `x-pti-client-id:${clientId}\n`;
    payload += urlParser.parse(url).pathname;
    console.log(payload);
    return signPayload(clientId, key, payload);
}

function signPayload(clientId, key, payload) {
    const KJUR = pm.collectionVariables.get('KJUR');
    const KEYUTIL = pm.collectionVariables.get('KEYUTIL');

    const jwk = JSON.parse(key);
    const rsaKey = KEYUTIL.getKey(jwk);
    const thumbprint = KJUR.jws.JWS.getJWKthumbprint(jwk);
    console.log(thumbprint);
    const header = { kid: thumbprint, cid: clientId };
    return KJUR.jws.JWS.sign("RS512", header, payload, rsaKey);
}

function removeComments(string){
    //Takes a string of code, not an actual function.
    return string.replace(/\/\*[\s\S]*?\*\/|(?<=[^:])\/\/.*|^\/\/.*/g,'').trim();//Strip comments
}

function main() {
    pm.collectionVariables.set("x-pti-session-id", uuid.v4());
    pm.collectionVariables.set("x-pti-request-id", uuid.v4());
    pm.collectionVariables.set("dynamicUuid", uuid.v4());

    const clientId = pm.collectionVariables.get("x-pti-client-id");
    const privateKey = pm.collectionVariables.get("private-key");
    const url = pm.variables.replaceIn(pm.request.url.toString());
    console.log(url);
    const method = pm.request.method;
    const time = moment.utc();
    const date = time.format('ddd, DD MMM YYYY HH:mm:ss [GMT]');
    const txDate = time.format('YYYY-MM-DDTHH:mm:ssZ');
    pm.collectionVariables.set('Date', date);
    pm.collectionVariables.set("transaction-date",txDate);
    console.log(pm.collectionVariables.get("transaction-date"));
    const body = removeComments(pm.variables.replaceIn(pm.request.body.raw) || "");
    console.log(body);
    const signature = getSignature(clientId, privateKey, url, method, body, date);

    pm.collectionVariables.set("x-pti-signature", signature);
}

loadJsrsasignLibrary().then(() => {
    //console.log("Jsrsasign library loaded successfully");
    main();
}).catch((err) => {
    console.error("Error during script execution:", err);
});
